class Solution {
public:
    int longestValidParentheses(string s) {
        return this->solution1(s);
    }
    
private:
    // 方法一：遍历两次。
    // 第一次记录那些位置的括号字符匹配了，第二次检查至多有多少连续匹配的字符。
    int solution1(string s) {
        int s_len = (int)s.size();
        
        if (s_len <= 1) {
            return 0;
        }
        
        if (s_len == 2) {
            if (s == "()") {
                return 2;
            } else {
                return 0;
            }
        }
        
        int res = 0;
        
        // 栈，存储 <括号下标, 括号> （本题只有左右小括号，所以简单地只用 stack<int> 记录左括号下标也行）
        stack<pair<int, char>> parentheses_stack = {};
        // 记录某下标的括号是否匹配
        const int EXTRA_SPACE = 10; // 如果 EXTRA_SPACE <= 0，LeetCode 会有越界报错。此处最小设为 1
        vector<bool> match_list = vector<bool>((int)s.size() + EXTRA_SPACE, false);
        
        // 遍历第一遍，记录有哪些位置的字符是能够匹配的，在 match_list 里记为 true
        for (int i = 0; i < s_len; i++) {
            if (s[i] == '(') {
                // 如果是左括号，则加进来，但不增加 res 长度
                parentheses_stack.push({i, '('});
            } else if (s[i] == ')') {
                // 如果是右括号，检验左右括号匹配情况
                if (parentheses_stack.empty() || parentheses_stack.top().second != '(') {
                    // 若不匹配，看下一个循环
                    continue;
                } else {
                    // 若匹配，记录匹配的两个字符，在 match_list 里记为 true
                    match_list[parentheses_stack.top().first] = true;
                    match_list[i] = true;
                    
                    // 弹出栈顶的一个 '('
                    parentheses_stack.pop();
                }
            } else {
                // 其它字符，本题不处理
                continue;
            }
        }
        
        // 遍历第二遍，查看 match_list 至多有多少连续的 true
        int cur_len = 0;
        for (int i = 0; i < s_len; i++) {
            if (match_list[i]) {
                while (match_list[i]) {
                    cur_len ++;
                    i ++;
                }
                if (cur_len > res) {
                    res = cur_len;
                }
                cur_len = 0;
            }
        }
        
        return res;
    }
    
    // 方法二：一遍扫描完成。超时，在连续出现 '(' 的情况下，执行效率很差，基本退化到 O(n^2)
    int solution2(string s) {
        int s_len = (int)s.size();
        
        if (s_len <= 1) {
            return 0;
        }
        
        if (s_len == 2) {
            if (s == "()") {
                return 2;
            } else {
                return 0;
            }
        }
        
        int res = 0;
        
        for (int i = 0; i < s_len; i++) {
            int cur_len = 0;
            // 如果子串起始字符是 '('，那么从该字符开始查找一个有效的括号子串
            if (s[i] == '(') {
                cur_len = this->findValidParentheses(s, i);
                if (cur_len > 0) {
                    if (cur_len > (int)(s_len / 2)) {
                        // 如果本次得到的有效括号子串长度超过 s 的一半，那它肯定是最长的了
                        return cur_len;
                    } else if (cur_len > res) {
                        // 如果当前获得的有效括号子串长度比 res 高，则更新最优解
                        res = cur_len;
                    }
                    i += cur_len - 1; // 跳过已探索的部分
                }
            } else {
                continue;
            }
        }
        
        return res;
    }
    
    int findValidParentheses (string s, int start_index) {
        int res = 0;
        stack<char> parentheses_stack = {};
        int s_len = (int)s.size();
        
        for (int i = start_index; i < s_len; i++) {
            if (s[i] == '(') {
                // 如果是左括号，则加进来，但不增加 res 长度
                parentheses_stack.push('(');
            } else if (s[i] == ')') {
                if (parentheses_stack.empty() || parentheses_stack.top() != '(') {
                    // 出现不匹配，返回当前结果
                    return res;
                } else {
                    // 弹出栈顶的一个 '('
                    parentheses_stack.pop();
                    // 只有在匹配左右括号，并且栈空时，才记录 res 长度
                    if (parentheses_stack.empty()) {
                        res = i - start_index + 1;
                    }
                }
            } else {
                // 其它字符，本题不处理
                continue;
            }
        }
        
        return res;
    }
};


